import React from "react";
import { useState, useEffect } from "react";
import "./body.css";

export const img_300 = "https://image.tmdb.org/t/p/w300";
export const unavailable =
  "https://www.movienewz.com/img/films/poster-holder.jpg";

const Body = () => {
  const [state, setState] = useState([]); //initializing the state variable as an empty array
  const fetchTrending = async () => {
    try {
      const response = await fetch(
        `https://api.themoviedb.org/3/trending/all/day?api_key=${process.env.REACT_APP_TMDB_API_KEY}`
      );

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // fetching data from API in JSON Format
      console.log(data); // Log the data to see what is being returned
      setState(data.results); //storing that data in the state
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchTrending(); //calling the fetchTrending function only during the initial rendering of the app.
  }, []);
  return (
    <>
      <div className="align-items-center d-flex justify-content-center">
        <div className="row w-100">
          <div className="col-12">
            <h1 className="text-center text-light bg-dark position-fixed"></h1>
          </div>
        </div>
      </div>
      <div className="container-fluid">
        <div className="row">
          {state.map((item) => {
            const {
              id,
              poster_path,
              title,
              name,
              media_type,
              first_air_date,
              release_date,
            } = item;
            return (
              <div key={id} className="col-md-3 col-sm-6 mb-4">
                <div className="card bg-dark h-100">
                  <img
                    src={
                      poster_path ? `${img_300}/${poster_path}` : unavailable
                    }
                    className="card-img-top pt-3 pb-0 px-3"
                    alt={title || name}
                  />
                  <div className="card-body">
                    <h5 className="card-title text-center text-light fs-5">
                      {title || name}
                    </h5>
                    <div className="d-flex fs-6 align-items-center text-light justify-content-evenly movie">
                      <div>{media_type === "tv" ? "TV" : "Movie"}</div>
                      <div>{first_air_date || release_date}</div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default Body;
