import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFilm, faPerson } from "@fortawesome/free-solid-svg-icons";
import "./footer.css";

const Footer = () => {
  const data = [
    {
      icon: faFilm,
      text: "Home",
      link: "/",
    },
    {
      icon: faPerson,
      text: "Account",
      link: "/account",
    },
    {
      icon: faFilm,
      text: "Movies",
      link: "/movies",
    },
    {
      icon: faPerson,
      text: "TV shows",
      link: "/tv",
    },
  ];
  return (
    <>
      <div className="container-fluid">
        <div className="row">
          <div className="col-12 text-center bg-dark footer">
            {data.map((Val) => {
              return (
                <>
                  <button className="col-sm-2 col-md-2 btn btn-dark h-100">
                    <FontAwesomeIcon icon={Val.icon} id="fire" />
                    <br />
                    <h5 className="pt-1 fs-6">{Val.text}</h5>
                  </button>
                </>
              );
            })}
            ;
          </div>
        </div>
      </div>
    </>
  );
};

export default Footer;
