import React from "react";
import { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faYoutube } from "@fortawesome/free-brands-svg-icons";
import YouTube from "react-youtube";

function VideoPlayer({ videoid, onPlayerVisibleChange }) {
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);
  const [opts, setOpts] = useState({
    height: "390",
    width: "640",
    playerVars: {
      autoplay: 0,
      controls: 1,
    },
  });

  useEffect(() => {
    onPlayerVisibleChange(isPlayerVisible);
  }, [isPlayerVisible, onPlayerVisibleChange]);

  const handleLogoClick = () => {
    isPlayerVisible ? setIsPlayerVisible(true) : setIsPlayerVisible(false);
  };

  const closePlayer = () => {
    setIsPlayerVisible(false);
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 640) {
        setOpts({
          height: "195",
          width: "320",
          playerVars: {
            autoplay: 0,
            controls: 1,
          },
        });
      } else if (window.innerWidth < 1024) {
        setOpts({
          height: "293",
          width: "480",
          playerVars: {
            autoplay: 0,
            controls: 1,
          },
        });
      } else {
        setOpts({
          height: "390",
          width: "640",
          playerVars: {
            autoplay: 0,
            controls: 1,
          },
        });
      }
    };

    window.addEventListener("resize", handleResize);
    handleResize(); // Call the function initially to set the correct size

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <>
      <div className="flex flex-col">
        <button
          onClick={handleLogoClick}
          className=" play-button border-none bg-transparent text-slate-50 text-center p-2 rounded-full"
        >
          <p className="text-slate-50 text-center">Trailer</p>
          <FontAwesomeIcon icon={faYoutube} className="fa-inverse fa-3x" />
        </button>

        {/* Full-Screen YouTube Player */}
        {isPlayerVisible && (
          <div className="fullscreen-overlay" onClick={closePlayer}>
            <YouTube
              videoId={videoid}
              opts={opts}
              className="youtube-player border-none  align-middle"
            />
          </div>
        )}
      </div>
    </>
  );
}

export default VideoPlayer;
