import React, { useState, useEffect } from "react";
import "./body.css";

const Body = () => {
  const [movies, setMovies] = useState([]); // State for movies
  const [tvShows, setTvShows] = useState([]); // State for TV shows
  const [movieLogos, setMovieLogos] = useState({}); // State for movie logos

  const fetchTrendingMovies = async () => {
    const url =
      "https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc";
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3MDRiOGY5NGIzZTBmMWEyMzk3MGEyNWMyMDAyZjI0YyIsIm5iZiI6MTczMTgyMDk5OC41MzYzNTM2LCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.tAz9nR91UBqwEeqKwmRNn24oyjlkHyGQKxaYPTFuifE",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format
      console.log(data); // Log the data to see what is being returned
      setMovies(data.results); // Storing that data in the state

      // Fetch logos for each movie
      data.results.forEach((movie) => {
        fetchMovieLogo(movie.id);
      });
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const fetchMovieLogo = async (id) => {
    const url = `https://api.themoviedb.org/3/movie/${id}/images/logos?image_language=eng`;
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3MDRiOGY5NGIzZTBmMWEyMzk3MGEyNWMyMDAyZjI0YyIsIm5iZiI6MTczMjI3OTU2My45MDYxOTI4LCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.9CX3jAkZFXpphWrUjO-2dlRPYpjGgYjB-iAhevsH8Ow",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format
      console.log(data); // Log the data to see what is being returned

      // Extract the logo URL
      const logo =
        data.logos && data.logos.length > 0 ? data.logos[0].file_path : null;

      // Update the state with the logo URL
      setMovieLogos((prevLogos) => ({
        ...prevLogos,
        [id]: logo,
      }));
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const fetchTrendingTV = async () => {
    const url =
      "https://api.themoviedb.org/3/discover/tv?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc";
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3MDRiOGY5NGIzZTBmMWEyMzk3MGEyNWMyMDAyZjI0YyIsIm5iZiI6MTczMTgyMDk5OC41MzYzNTM2LCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.tAz9nR91UBqwEeqKwmRNn24oyjlkHyGQKxaYPTFuifE",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format
      console.log(data); // Log the data to see what is being returned
      setTvShows(data.results); // Storing that data in the state
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchTrendingMovies(); // Calling the fetchTrendingMovies function only during the initial rendering of the app.
    fetchTrendingTV(); // Calling the fetchTrendingTV function only during the initial rendering of the app.
  }, []);

  return (
    <>
      <div className="container-fluid d-flex align-items-center justify-content-center">
        <div className="row w-100">
          <div className="col-12 d-flex align-items-center justify-content-center">
            <h1 className="text-center text-light">Popular Movies</h1>
          </div>
        </div>
      </div>
      <div className="container-fluid">
        <div className="row">
          {movies.map((item) => {
            <MovieCard key={item.id} item={item} movieLogos={movieLogos} />;
          })}
        </div>
      </div>
      <div className="container-fluid d-flex align-items-center justify-content-center">
        <div className="row w-100">
          <div className="col-12 d-flex align-items-center justify-content-center">
            <h1 className="text-center text-light">Popular TV Shows</h1>
          </div>
        </div>
      </div>
      <div className="container-fluid">
        <div className="row">
          {tvShows.map((item) => {
            const {
              id,
              poster_path,
              title,
              name,
              media_type,
              first_air_date,
              release_date,
            } = item;
            return (
              <div key={id} className="col-md-3 col-sm-6 mb-4">
                <div className="card bg-dark h-100">
                  <img
                    src={
                      poster_path ? `${img_300}/${poster_path}` : unavailable
                    }
                    className="card-img-top pt-3 pb-0 px-3"
                    alt={title || name}
                  />
                  <div className="card-body">
                    <h5 className="card-title text-center text-light fs-5 ">
                      {title || name}
                    </h5>
                    <div className="d-flex fs-6 align-items-center justify-content-evenly movie">
                      <div>{media_type === "tv" ? "TV" : "Movie"}</div>
                      <div>{first_air_date || release_date}</div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default Body;
