import React, { useState, useEffect, useCallback } from "react";
import MovieCard from "./movieCard";

const Body = () => {
  const [movies, setMovies] = useState([]); // State for movies
  const [tvShows, setTvShows] = useState([]); // State for TV shows

  const fetchTrendingMovies = useCallback(async () => {
    const url =
      "https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc";
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIwYmY1MWZjZDYzYTJmZWExODNmYzBlN2QxY2U5Y2IyYyIsIm5iZiI6MTczMjMzMTI0Ny41MDMzNTYyLCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.P8aqYptE9vZsLN9E3SMmHrEVi12mkUxXz1PRDe8j3_8",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format

      // Fetch logos for each movie and update the movie object
      const moviesWithLogos = await Promise.all(
        data.results.map(async (movie) => {
          const logo = await fetchMovieLogo(movie.id);
          return { ...movie, logo };
        })
      );

      // Fetch video key for each movie and update the movie object
      const moviesWithVideos = await Promise.all(
        moviesWithLogos.map(async (movie) => {
          const videoKey = await fetchMovieVideos(movie.id);
          return { ...movie, videoKey };
        })
      );

      setMovies(moviesWithVideos); // Storing that data in the state
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }, []); // Empty dependency array ensures the function is memoized

  const fetchMovieLogo = async (id) => {
    const url = `https://api.themoviedb.org/3/movie/${id}/images?language=en`;
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIwYmY1MWZjZDYzYTJmZWExODNmYzBlN2QxY2U5Y2IyYyIsIm5iZiI6MTczMjMzMTI0Ny41MDMzNTYyLCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.P8aqYptE9vZsLN9E3SMmHrEVi12mkUxXz1PRDe8j3_8",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format

      // Extract the logo URL
      const logo =
        data.logos && data.logos.length > 0 ? data.logos[0].file_path : null;
      return logo;
    } catch (error) {
      console.error("Error fetching data:", error);
      return null;
    }
  };

  const fetchMovieVideos = async (id) => {
    const url = `https://api.themoviedb.org/3/movie/${id}/videos?language=en-US`;
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIwYmY1MWZjZDYzYTJmZWExODNmYzBlN2QxY2U5Y2IyYyIsIm5iZiI6MTczMjMzMTI0Ny41MDMzNTYyLCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.P8aqYptE9vZsLN9E3SMmHrEVi12mkUxXz1PRDe8j3_8",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format

      const teasers = data.results.filter((video) => video.type === "Trailer");

      // Check if teasers array is not empty before accessing the key property
      const videoKey = teasers.length > 0 ? teasers[0].key : null;
      return videoKey;
    } catch (error) {
      console.error("Error fetching data:", error);
      return null;
    }
  };

  const fetchTrendingTV = useCallback(async () => {
    const url =
      "https://api.themoviedb.org/3/discover/tv?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc";
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIwYmY1MWZjZDYzYTJmZWExODNmYzBlN2QxY2U5Y2IyYyIsIm5iZiI6MTczMjMzMTI0Ny41MDMzNTYyLCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.P8aqYptE9vZsLN9E3SMmHrEVi12mkUxXz1PRDe8j3_8",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json(); // Fetching data from API in JSON Format
      setTvShows(data.results); // Storing that data in the state
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }, []); // Empty dependency array ensures the function is memoized

  useEffect(() => {
    fetchTrendingMovies(); // Calling the fetchTrendingMovies function only during the initial rendering of the app.
    fetchTrendingTV(); // Calling the fetchTrendingTV function only during the initial rendering of the app.
  }, [fetchTrendingMovies, fetchTrendingTV]); // Added fetchTrendingMovies and fetchTrendingTV to the dependency array

  return (
    <>
      <div className="bg-black px-4">
        <div className="">
          <h1 className="text-center text-light text-2xl tracking-widest py-2">
            Popular Movies
          </h1>
          <div className="flex space-x-4 px-0 overflow-x-auto py-4 md:py-2 xl:py-5 pe-4">
            {movies.map((item) => (
              <div
                key={item.id}
                className="flex-none w-[10rem] md:w-[12rem] lg:w-[13rem] xl:w-[15rem]"
              >
                <MovieCard movie={item} />
              </div>
            ))}
          </div>
        </div>
        <div>
          <div>
            <div>
              <h1 className="text-center text-light text-2xl tracking-widest bg-black py-2">
                Popular TV Shows
              </h1>
            </div>
          </div>
        </div>
        <div className=" bg-black">
          <div className="flex space-x-4 px-0 overflow-x-auto py-4 md:py-2 xl:py-5 pe-4">
            {tvShows.map((item) => (
              <div
                key={item.id}
                className="flex-none w-[10rem] md:w-[12rem] lg:w-[13rem] xl:w-[15rem]"
              >
                <MovieCard movie={item} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default Body;
