import React, { useState, useEffect } from "react";
import "./body.css";

const img_300 = "https://image.tmdb.org/t/p/w300"; // Base URL for images
const unavailable = "https://www.movienewz.com/img/films/poster-holder.jpg"; // Path to a placeholder image

const Body = () => {
  const [state, setState] = useState([]); // Initializing the state variable as an empty array

  const fetchTrendingMovie = async () => {
    const url =
      "https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc";
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3MDRiOGY5NGIzZTBmMWEyMzk3MGEyNWMyMDAyZjI0YyIsIm5iZiI6MTczMTgyMDk5OC41MzYzNTM2LCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.tAz9nR91UBqwEeqKwmRNn24oyjlkHyGQKxaYPTFuifE",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format
      console.log(data); // Log the data to see what is being returned
      setState(data.results); // Storing that data in the state
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchTrendingMovie(); // Calling the fetchTrending function only during the initial rendering of the app.
  }, []);

  const fetchTrendingTV = async () => {
    const url =
      "https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc";
    const options = {
      method: "GET",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3MDRiOGY5NGIzZTBmMWEyMzk3MGEyNWMyMDAyZjI0YyIsIm5iZiI6MTczMTgyMDk5OC41MzYzNTM2LCJzdWIiOiI2NzM4OWRlNjljMTZkYWZhMDZmOWExMmEiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.tAz9nR91UBqwEeqKwmRNn24oyjlkHyGQKxaYPTFuifE",
      },
    };

    try {
      const response = await fetch(url, options);

      // Check if the response status is OK
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json(); // Fetching data from API in JSON Format
      console.log(data); // Log the data to see what is being returned
      setState(data.results); // Storing that data in the state
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchTrendingTV(); // Calling the fetchTrending function only during the initial rendering of the app.
  }, []);

  return (
    <>
      <div className="container-fluid d-flex align-items-center justify-content-center">
        <div className="row w-100">
          <div className="col-12">
            <h1 className="text-center text-light">Popular</h1>
          </div>
        </div>
      </div>
      <div className="container-fluid">
        <div className="row">
          {state.map((item) => {
            const {
              id,
              poster_path,
              title,
              name,
              media_type,
              first_air_date,
              release_date,
            } = item;
            return (
              <div key={id} className="col-md-3 col-sm-6 mb-4">
                <div className="card bg-dark h-100">
                  <img
                    src={
                      poster_path ? `${img_300}/${poster_path}` : unavailable
                    }
                    className="card-img-top pt-3 pb-0 px-3"
                    alt={title || name}
                  />
                  <div className="card-body">
                    <h5 className="card-title text-center text-light fs-5">
                      {title || name}
                    </h5>
                    <div className="d-flex fs-6 align-items-center justify-content-evenly movie">
                      <div>{media_type === "tv" ? "TV" : "Movie"}</div>
                      <div>{first_air_date || release_date}</div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default Body;
