import React from "react";
import { useLocation } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faYoutube } from "@fortawesome/free-brands-svg-icons";

const img_500 = "https://image.tmdb.org/t/p/w500"; // Base URL for images
const img_1280 = "https://image.tmdb.org/t/p/w1280"; // Base URL for higher resolution images

const unavailable = "https://www.movienewz.com/img/films/poster-holder.jpg"; // Path to a placeholder image

const SingleMovie = () => {
  const location = useLocation();
  const { movie } = location.state || {};
  console.log(movie);

  if (!movie) {
    return <div>Movie not found</div>;
  }

  return (
    <div className="flex flex-col main-container pt-3 pb-10 min-h-screen bg-black">
      <h1 className="text-center text-4xl mb-4"></h1>
      <img
        src={movie.logo ? `${img_500}/${movie.logo}` : unavailable}
        alt={movie.title}
        className="mx-auto pb-3 max-h-24 px-4"
      />
      <div className="movie-details-container flex flex-col">
        <img
          src={
            movie.backdrop_path
              ? `${img_1280}/${movie.backdrop_path}`
              : unavailable
          }
          className="backdrop-image mx-auto w-full"
        />
        <p className="mt-4 text-white px-3 font-light pb-10">
          {movie.overview}
        </p>
        <p>Trailer</p>
        <FontAwesomeIcon icon={faYoutube} className="fa-inverse fa-3x" />
      </div>
    </div>
  );
};

export default SingleMovie;
