import React from "react";
import { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faYoutube } from "@fortawesome/free-brands-svg-icons";
import YouTube from "react-youtube";

function VideoPlayer({ videoid, onPlayerVisibleChange }) {
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);
  console.log("Video ID:", videoid); // Debugging: Check if videoid is received correctly

  useEffect(() => {
    onPlayerVisibleChange(isPlayerVisible);
  }, [isPlayerVisible, onPlayerVisibleChange]);

  const handleLogoClick = () => {
    setIsPlayerVisible(true);
    console.log(`https://www.youtube.com/embed/${videoid}?autoplay=1`);
  };

  const closePlayer = () => {
    setIsPlayerVisible(false);
  };

  const opts = {
    height: "390",
    width: "640",
    playerVars: {
      autoplay: 0,
    },
  };

  return (
    <>
      <div className="flex flex-col">
        <button
          onClick={handleLogoClick}
          className=" play-button border-none bg-transparent text-slate-50 text-center p-2 rounded-full"
        >
          <p className="text-slate-50 text-center">Trailer</p>
          <FontAwesomeIcon icon={faYoutube} className="fa-inverse fa-3x" />
        </button>

        {/* Full-Screen YouTube Player */}
        {isPlayerVisible && (
          <div className="fullscreen-overlay" onClick={closePlayer}>
            <YouTube
              videoId={videoid}
              opts={opts}
              className="youtube-player border-none  align-middle"
            />
          </div>
        )}
      </div>
    </>
  );
}

export default VideoPlayer;
