import React from "react";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faYoutube } from "@fortawesome/free-brands-svg-icons";

function VideoPlayer({ videoid }) {
  const videoId = videoid;
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);

  const handleLogoClick = () => {
    setIsPlayerVisible(true);
  };

  const closePlayer = () => {
    setIsPlayerVisible(false);
  };

  return (
    <>
      <div>
        <button
          onClick={handleLogoClick}
          className=" play-button border-none bg-transparent"
        >
          <p className="text-slate-50 text-center">Trailer</p>
          <FontAwesomeIcon icon={faYoutube} className="fa-inverse fa-3x" />
        </button>

        {/* Full-Screen YouTube Player */}
        {isPlayerVisible && (
          <div className="fullscreen-overlay" onClick={closePlayer}>
            <iframe
              src={`https://www.youtube.com/embed/${videoId}?autoplay=1`}
              allow="autoplay; encrypted-media"
              allowFullScreen
              title="YouTube Video Player"
              className="youtube-player border-none w-full max-w-5xl h-[80vh]"
            ></iframe>
          </div>
        )}
      </div>
    </>
  );
}

export default VideoPlayer;
