import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import VideoPlayer from "../components/videoPlayer";

const img_500 = "https://image.tmdb.org/t/p/w500"; // Base URL for images
const img_1280 = "https://image.tmdb.org/t/p/w1280"; // Base URL for higher resolution images

const unavailable = "https://www.movienewz.com/img/films/poster-holder.jpg"; // Path to a placeholder image

const SingleMovie = () => {
  const location = useLocation();
  const { movie } = location.state || {};
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);

  console.log(movie);

  if (!movie) {
    return <div>Movie not found</div>;
  }

  const handlePlayerVisibleChange = (visible) => {
    setIsPlayerVisible(visible);
  };

  return (
    <div className="flex flex-col main-container pt-3 pb-20 min-h-screen bg-black">
      <h1 className="text-center text-4xl mb-4">{movie.title}</h1>
      <img
        src={movie.logo ? `${img_500}/${movie.logo}` : unavailable}
        alt={movie.title}
        className="mx-auto pb-3 max-h-24 px-4"
      />
      <div className="movie-details-container flex flex-col">
        <img
          src={
            movie.backdrop_path
              ? `${img_1280}/${movie.backdrop_path}`
              : unavailable
          }
          alt={movie.title}
          className="backdrop-image mx-auto w-full"
        />
        <p className="mt-4 text-white px-3 font-light pb-10">
          {movie.overview}
        </p>
        <div
          className={`flex flex-col ${
            isPlayerVisible ? "w-full" : "w-1/4"
          } mx-auto`}
        >
          <VideoPlayer
            videoid={movie.videoid}
            onPlayerVisibleChange={handlePlayerVisibleChange}
          />
        </div>
      </div>
    </div>
  );
};

export default SingleMovie;
